import React from "react";
import Accordion, {useAccordionButton} from "react-bootstrap/Accordion";
import "./Card.css";
function Card(props) {

  
function ContextAwareToggle({ children, eventKey, callback }) {
  const { activeEventKey } = useContext(AccordionContext);

  const decoratedOnClick = useAccordionButton(
    eventKey,
    () => callback && callback(eventKey),
  );

  const isCurrentEventKey = activeEventKey === eventKey;

  return (
    <button
      type="button"
      style={{ backgroundColor: isCurrentEventKey ? 'pink' : 'lavender' }}
      onClick={decoratedOnClick}
    >
      {children}
    </button>
  );
}

  return (
    <div className="card-wrapper">
      <Accordion defaultActiveKey="0">
        <Accordion.Item eventKey={props.ekey}>
          <Accordion.Header>
            <div className="container-fluid">
              <div className="row">
                <div className="col-sm-2">
                  <p className="card-date">{props.startDate}</p>
                </div>
                <div className="col-sm-2">
                  <p className="card-date">{props.endDate}</p>
                </div>
                <div className="col-sm-4">
                  <p className="card-date"></p>
                </div>
              </div>
              <div className="row">
                <div className="col-sm-6">
                  <h6 className="card-position">{props.position}</h6>
                </div>
                <div className="col-sm-6 text-end">
                  <em className="card-location">{props.location}</em>
                </div>
              </div>
            </div>
          </Accordion.Header>
          <Accordion.Body>
            <ul className="detail-list">
              <li>{props.list1}</li>
              <li>{props.list2}</li>
              <li>{props.list3}</li>
            </ul>
          </Accordion.Body>
        </Accordion.Item>
      </Accordion>
    </div>
  );
}

export default Card;
